function sagepointer(id) {
	this.id = id;
	this.label = "";
	this.color = [255, 255, 255];
	this.left = 0;
	this.top = 0;
	this.visible = false;
}

sagepointer.prototype.start = function(label, color) {
	this.label = label;
	this.color = color;
	this.left = 0;
	this.top = 0;
	this.visible = true;
};

sagepointer.prototype.stop = function() {
	this.visible = false;
};


module.exports = sagepointer;
